import {
    arrayToSudoku,
    printSudoku,
    NakedPairsSolver,
    XYWingSolver,
    SimpleSolver,
    cloneSudoku,
    checkSudokuDifferent,
    MiscellaneousSolver
} from '@sudoku/solver/solver.js';

// 示例数独输入
let sudokuArray = [
    [5, 3, 0, 0, 7, 0, 0, 0, 0],
    [6, 0, 0, 1, 9, 5, 0, 0, 0],
    [0, 9, 8, 0, 0, 0, 0, 6, 0],
    [8, 0, 0, 0, 6, 0, 0, 0, 3],
    [4, 0, 0, 8, 0, 3, 0, 0, 1],
    [7, 0, 0, 0, 2, 0, 0, 0, 6],
    [0, 6, 0, 0, 0, 0, 2, 8, 0],
    [0, 0, 0, 4, 1, 9, 0, 0, 5],
    [0, 0, 0, 0, 8, 0, 0, 7, 9]
];

sudokuArray = [
    [6, 0, 0, 0, 0, 0, 0, 0, 8],
    [5, 0, 0, 9, 0, 8, 0, 0, 7],
    [8, 2, 0, 0, 0, 1, 0, 3, 0],
    [3, 4, 0, 2, 0, 9, 0, 8, 0],
    [2, 0, 0, 0, 8, 0, 3, 0, 0],
    [1, 8, 0, 3, 0, 7, 0, 2, 5],
    [7, 5, 0, 4, 0, 0, 0, 9, 2],
    [9, 0, 0, 0, 0, 5, 0, 0, 4],
    [4, 0, 0, 0, 9, 0, 0, 0, 3]
];

let sudoku = arrayToSudoku(sudokuArray);

// SimpleSolver

let otherSudoku = cloneSudoku(sudoku);

sudoku = new SimpleSolver().solver(sudoku);

checkSudokuDifferent(sudoku, otherSudoku);

// NakedPairsSolver

otherSudoku = cloneSudoku(sudoku);

sudoku = new NakedPairsSolver().solver(sudoku);

checkSudokuDifferent(sudoku, otherSudoku);

// MiscellaneousSolver

otherSudoku = cloneSudoku(sudoku);

sudoku = new MiscellaneousSolver().solver(sudoku);

checkSudokuDifferent(sudoku, otherSudoku);

// XYWingSolver

otherSudoku = cloneSudoku(sudoku);

sudoku = new XYWingSolver().solver(sudoku);

checkSudokuDifferent(sudoku, otherSudoku);